<template>
  <div class="vue-video">
    <video hidden id="dcVideo" preload="auto" autoplay="autoplay" controls="controls">
      <source id="dcSource" src="" type="video/mp4">
    </video>
    <div class="video video-play " :data-position="position" :class="{loading:hasLoad}" white v-if="source"
         v-on:click="playVideo(source)">
    </div>
  </div>

</template>

<script>

  export default {
    data(){
      return {
        hasLoad: false
      }
    },
    props: {
      source: {
        type: String,
        default: ''
      },
      position: {
        type: String,
        default: 'middle'
      }
    },
    methods: {
      playVideo: function (inPath) {
        this.hasLoad = true;
        document.getElementById('dcSource').src = inPath;
        document.getElementById('dcVideo').load();
        document.getElementById('dcVideo').play();
        setTimeout(() => {
          this.hasLoad = false;
        }, 500);
      }
    },
    watch: {},
    mounted(){
    }
  }
</script>
<style rel="stylesheet/scss" lang="scss">
  .video {
    background-repeat: no-repeat;
    background-size: contain;
    display: block;
    &-play {
      width: .98rem;
      height: .98rem;
      background-image: url("data:image/png;base64,");
      &[white] {
        width: .8rem;
        height: .8rem;
        background-image: url("data:image/png;base64,");
      }
      &[small] {
        width: .64rem;
        height: .64rem;
      }
      &.loading {
        background-image: url("data:image/gif;base64,");
      }
    }
    &[data-position='middle'] {
      position: absolute;
      z-index: 2;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
      -webkit-transform: translate(-50%, -50%);
    }
    &[data-position='right-bottom'] {
      position: absolute;
      z-index: 2;
      bottom: .2rem;
      right: .2rem;
    }
    &:after {
      content: '';
      position: absolute;
      top: -.2rem;
      right: -.2rem;
      bottom: -.2rem;
      left: -.2rem;
    }
  }

</style>
